mapper.update:
update dish
name = #{name}, category_id = #{categoryId}, price = #{price}, image = #{image}, description = #{description}, status = #{status}, update_user = #{updateUser}, update_time = #{updateTime},
where id = #{id}

mapper.select:
select d.*, c.name categoryName from dish d left join category c ON d.category_id = c.id


        and d.name like concat('%', #{name}, '%')


        and d.category_id = #{categoryId}


        and d.status = #{status}
